dynaForm.JS:
var dynaformOnload = function()
{ var oAux = document.getElementById('form[TASKS]');
	if(document.getElementById('form[PROCESS]').value=='')		
  	hideRowById(oAux);
  else
  	showRowById(oAux);	
};

leimnud.event.add(document.getElementById('form[PROCESS]'), 'change', function() {
  var oAux = document.getElementById('form[TASKS]');
  if (document.getElementById('form[PROCESS]').value=='')
  {    
     hideRowById(oAux);
  }
  else
  {
     showRowById(oAux);
  }
});
